import useInput from "../hooks/use-input"

const BasicForm = () => {

  const {
    value: enteredName,
    isValid: nameIsValid,
    hasError: nameHasError,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: nameBlurHandler,
    reset: nameFieldReset,
  } = useInput(value => value.trim() !== '')

  const {
    value: enteredPhone,
    valueIsValid: phoneIsValid,
    hasError: phoneHasError,
    valueChangeHandler: phoneChangeHandler,
    inputBlurHandler: phoneBlurHandler,
    reset: phoneFieldReset,
  } = useInput(value => value.trim() !== '')

  const {
    value: enteredEmail,
    valueIsValid: emailIsValid,
    hasError: emailHasError,
    valueChangeHandler: emailChangeHandler,
    inputBlurHandler: emailBlurHandler,
    reset: emailFieldReset,
  } = useInput(value => value.includes('@'))

  let formIsValid = false

  if (nameIsValid && phoneIsValid && emailIsValid) {
    formIsValid = true;
  }

  const submitHandler = (event) => {
    event.preventDefault()

    if (!formIsValid) {
      return;
    }

    nameFieldReset()
    phoneFieldReset()
    emailFieldReset()

  }




  return (
    <form onSubmit={submitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>Name</label>
          <input 
          type='text' id='name'
          value={enteredName}
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
           />
          {nameHasError && <p>Name cannot be blank.</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='phone'>Phone</label>
          <input 
          type='text' id='phone' 
          value={enteredPhone}
          onChange={phoneChangeHandler}
          onBlur={phoneBlurHandler}
          />
          {phoneHasError && <p>Phone cannot be blank.</p>}
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input 
        type='text' id='name'
        value={enteredEmail}
        onChange={emailChangeHandler}
        onBlur={emailBlurHandler}
        />
        {emailHasError && <p>Please enter a valid email.</p>}
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
