import { useState, useRef } from "react"

const SimpleInput = (props) => {

  const nameInputRef = useRef();
  const [enteredName, setenteredName] = useState('')
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false)

  const nameInputChangeHandler = event => {
    setenteredName(event.target.value)
  }

  const formSubmissionHandler = event => {
    event.preventDefault()

    if (enteredName.trim() === '') {
      setEnteredNameIsValid(false)
      return; //returns from the function so the rest of the function wont be executed
    }

    setEnteredNameIsValid(true)
    console.log(enteredName);

    const enteredValue = nameInputRef.current.value;
    console.log(enteredValue)

    //nameInputRef.current.value = "" -> not ideal because we are directly manipulating the dom
    //setenteredName("") -> this way is OK but not very clean
  }

  const nameInputClasses = enteredNameIsValid 
    ? 'form-control' 
    : 'form-control invalid'

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input ref={nameInputRef} type='text' id='name' onChange={nameInputChangeHandler} value={enteredName}/>
        {!enteredNameIsValid && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
